from tkinter import *from tkinter import filedialogfrom PIL import ImageTk, Imageimport osimport webbrowserroot = Tk()root.geometry("600x400")open_img = ImageTk.PhotoImage(Image.open ("open.png"))save_img = ImageTk.PhotoImage(Image.open ("save.png"))exit_img = ImageTk.PhotoImage(Image.open ("exit.jpg"))run_img = ImageTk.PhotoImage(Image.open ("pinkrun.png"))label = Label(root, text="File name")label.place(relx=0.28,rely=0.03,anchor= CENTER)text= Text(root,height=35,width=80)text.place(relx=0.5,rely=0.55,anchor= CENTER)input_file_name = Entry(root)input_file_name.place(relx=0.50,rely=0.03, anchor= CENTER)name=""def open_now():    global name    webbrowser.open(name)    my_text.delete(1.0, END)    input_file_name.delete(0, END)    text_file = filedialog.askopenfilename(title=" Open Text File", filetypes=(("Text Files", "*.txt"),))    print(text_file)    name = os.path.basename(text_file)    formated_name = name.split('.')[0]    input_file_name.insert(END,formated_name)    root.title(formated_name)    text_file = open(name,'r')    paragraph=text_file.read()    my_text.insert(END,paragraph)    text_file.close()    def save_now():    input_name = input_file_name.get()    file = open(input_name+".txt", "w")    data = my_text.get("1.0",END)    print(data)    file.write(data)    input_file_name.delete(0, END)    my_text.delete(1.0, END)    messagebox.showinfo("Update", "Success")        def run_now():    global file_path    webbrowser.open_new('file://' + file_path)    def exit_now():    root.destroy()    open1 = Button(root,image=open_img,command =open_now)save1 = Button(root,image=save_img, command = save_now)exit1 = Button(root,image=exit_img, command = exit_now)run1 = Button(root,image=run_img, command = run_now)open1.place(relx=0.05,rely=0.03,anchor=CENTER)save1.place(relx=0.11,rely=0.03,anchor= CENTER)exit1.place(relx=0.17,rely=0.03,anchor= CENTER)run1.place(relx=0.23,rely=0.03,anchor= CENTER)root.mainloop()